#!/bin/bash

echo "get key"
apt install -y gnupg
apt-key adv --keyserver keyserver.ubuntu.com --recv-keys 23E7166788B63E1E

echo "update dependencies"
apt-get update

echo "start install"
apt install -y gconf-service libasound2 libatk1.0-0 libc6 libcairo2 libcups2 --fix-missing
apt install -y libdbus-1-3 libexpat1 libfontconfig1 libgcc1 libgconf-2-4 libgdk-pixbuf2.0-0  --fix-missing
apt install -y libglib2.0-0 libgtk-3-0 libnspr4 libpango-1.0-0 libpangocairo-1.0-0 libstdc++6  --fix-missing
apt install -y libx11-6 libx11-xcb1 libxcb1 libxcomposite1 libxcursor1 libxdamage1 libxext6 libxfixes3  --fix-missing
apt install -y libxi6 libxrandr2 libxrender1 libxss1 libxtst6 ca-certificates fonts-liberation  --fix-missing
apt install -y libappindicator1 libnss3 lsb-release xdg-utils wget --fix-missing